import {useState, useEffect} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";

const styles = {
    formContainer: {
        display: "flex",
        flexDirection: "column"
    },
    formItem: {
        marginTop: "3px"
    },
    formSubmit: {
        padding: "5px",
        marginTop: "20px"
    },
    splitForm:{
        display: "flex",
        justifyContent:"space-between",
        width:"500px"
    }
}
const NewPet = () => {
    const navigate = useNavigate()

    const [error, setError] = useState({})
    const [petName, setPetName] = useState("")
    const [petType, setPetType] = useState("")
    const [petDescription, setPetDescription] = useState("")
    const [petSkills, setPetSkills] = useState([])

    const submitHandler = (e) => {

        const newPet = {
            petName, petType, petDescription, petSkills
        }

        axios.post("http://localhost:8080/api/pets", newPet)
            .then(res => navigate("/pets")
            )
            .catch(err => {
                setError(err.response.data.errors)
            })
    }

    return (
        <div>
            <h3>Know a pet needing a home?</h3>
            <div>
                <form onSubmit={submitHandler} style={styles.splitForm}>

                    <div style={styles.formContainer}>
                        <label>Name:</label>
                        <input style={styles.formItem} type="text" onChange={(e) => setPetName(e.target.value)}/>
                        {error.name && <p>{error.name.message}</p>}

                        <label>Type: </label>
                        <input style={styles.formItem} type="text" onChange={(e) => setPetType(e.target.value)}/>
                        {error.type && <p>{error.type.message}</p>}

                        <label style={styles.formItem}>Description:</label>
                        <input type="number" min={0} onChange={(e) => setPetDescription(e.target.value)}/>
                        {error.description && <p>{error.description.message}</p>}
                    </div>

                    <div style={styles.formContainer}>
                        <label>Name:</label>
                        <input type="text" onChange={(e) => setPetName(e.target.value)}/>
                        {error.name && <p>{error.name.message}</p>}

                        <label>Type: </label>
                        <input type="text" onChange={(e) => setPetType(e.target.value)}/>
                        {error.type && <p>{error.type.message}</p>}

                        <label style={styles.formItem}>Description:</label>
                        <input type="number" min={0} onChange={(e) => setPetDescription(e.target.value)}/>
                        {error.description && <p>{error.description.message}</p>}
                        <input style={styles.formSubmit} type="submit"/>
                    </div>

                </form>
            </div>
        </div>
    )
}

export default NewPet
