import {useState, useEffect} from "react";
import axios from "axios";
import {useParams, useNavigate} from "react-router-dom";

const styles = {
    formContainer: {
        display: "flex",
        flexDirection: "column"
    },
    formItem: {
        marginTop: "3px"
    },
    formSubmit: {
        padding: "5px",
        marginTop: "20px"
    },
    splitForm: {
        display: "flex",
        justifyContent: "space-between",
        width: "500px"
    },
    topNav:{
        display:"flex",
        justifyContent: "space-between",
        paddingRight:"145px",
        alignItems:"center"
    },
    newAuthorLink: {
        color: "blue",
        textDecoration: "underline"
    }
}
const ViewPet = () => {
    const navigate = useNavigate()

    const {id} = useParams()
    const[temp, setTemp] =useState("")
    const [error, setError] = useState({})
    const [petName, setPetName] = useState("")
    const [petType, setPetType] = useState("")
    const [petDescription, setPetDescription] = useState("")
    const [petSkills, setPetSkills] = useState([])


    useEffect(() => {
        axios.get(`http://localhost:8080/api/pets/${id}`)
            .then(res =>{
                setPetName(res.data.petName)
                setPetType(res.data.petType)
                setPetDescription(res.data.petDescription)
                setPetSkills(res.data.petSkills)
                setTemp(res.data.petName)

            })
    }, []);


    const submitHandler = (e) => {
        e.preventDefault()
        const newPet = {
            petName, petType, petDescription, petSkills
        }


        axios.put(`http://localhost:8080/api/pets/${id}/edit`, newPet)
            .then(res => navigate("/pets")
            )
            .catch(err => {
                console.log(err)
                setError(err.response.data.errors)
                console.log(error)
            })
    }

    return (
        <div>
            <div style={styles.topNav}>
                <h1>Details about {temp}</h1>
                <button>Adopt Pet</button>
            </div>
            <div>
              <div>
                  <h4>Pet type:</h4>
                  <h4>{petType}</h4>
              </div>
            </div>
        </div>
    )
}

export default ViewPet
